import React from 'react'

const Settings = () => {

    const initialFormData = Object.freeze({
        portNumber: "",
        apiKey: "",
        zapAdress : ""
    });

    const [formData, updateFormData] = React.useState(initialFormData);

    const handleChange = (e) => {
        updateFormData({
            ...formData,

            // Trimming any whitespace
            [e.target.name]: e.target.value.trim()
        });
    };

    function httpGet(theUrl)
    {
        var xmlHttp = new XMLHttpRequest();
        xmlHttp.open( "GET", theUrl, false ); // false for synchronous request
        xmlHttp.send( null );
        return xmlHttp.responseText;
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        // console.log(formData);
        httpGet(formData)
        // ... submit to API or something
    };

    return (
            <div className="newUser">
        <h1 className="newUserTitle">Settings</h1>
        <form className="newUserForm">
            <div className="newUserItem">
                <label>OwaspZAP Port Number</label>
                <input type="text" placeholder="example:8080" onChange={handleChange}/>
            </div>
            <div className="newUserItem">
                <label>OwaspZAP API Key</label>
                <input type="text" placeholder="example:1234567890" onChange={handleChange}/>
            </div>
            <div className="newUserItem">
                <label>OwaspZAP Address</label>
                <input type="text" placeholder="example:localhost" onChange={handleChange}/>
            </div>
            <button onClick={handleSubmit} className="newScanButton">Apply</button>
        </form>
        </div>
    )
}

export default Settings
